import React from 'react';
import Name from './components/Name';
import Email from './components/Email';
import './App.css';

class App extends React.Component {
  constructor() {
    super()

    this.handlerGenerico = this.handlerGenerico.bind(this);

    this.state = {
      name: '',
      email: '',
    }
  }

  handlerGenerico({ target }) {
  const { name, value } = target;

  this.setState({
    [name]: value,
  })
  }

  render() {

    return (
      <form>
        <h1>Cadastre seu Curr√≠culo</h1>
          <fieldset>
            <legend>Dados Pessoais</legend>
            <Name name={ this.state.name } handlerGenerico={ this.handlerGenerico } />
            <Email name={ this.state.email } handlerGenerico={ this.handlerGenerico } />
          </fieldset>
      </form>
    );
  }
}

export default App;
