import React from 'react'; 

class Resume extends React.Component {
  render() {
    const { name, handlerGenerico } = this.props;
    return(
      <label>
        <input
        type='textarea'
        value={ name }
        name='textarea'
        onChange={ handlerGenerico }
        maxLength='1000'
        />
      </label>
    )
  }
}

export default Resume;
