import React from 'react';

class Name extends React.Component {
  render() {

    const { name, handlerGenerico, toUpperCaseFunction } = this.props

    return(
      <label>
      Nome:
        <input 
          type='text' 
          name='name' 
          value={ name } 
          maxLength='40'
          onChange={ handlerGenerico, toUpperCaseFunction } 
          placeholder='Digite o seu nome aqui'
        />
      </label>
    )
  }
}

export default Name;
