import React from 'react';
import Name from './components/Name';
import Email from './components/Email';
import CPF from './components/CPF';
import Address from './components/Address';
import './App.css';

class App extends React.Component {
  constructor() {
    super()

    this.handlerGenerico = this.handlerGenerico.bind(this);
    this.toUpperCaseFunction = this.toUpperCaseFunction.bind(this);

    this.state = {
      name: '',
      email: '',
      cpf: 0,
    }
  }

  toUpperCaseFunction(string) {
    return string.toUpperCase();
  }

  handlerGenerico({ target }) {
  const { name } = target;

    let value = (target.type === 'checkbox') ? target.checked : target.value;

    value = (name === 'name') ? this.toUpperCaseFunction(value) : value; 

    this.setState({
      [name]: value,
    })
  }

  render() {

    return (
      <form>
        <h1>Trabalhe Conosco</h1>
          <fieldset>
            <legend>Dados Pessoais</legend>
              <div className="name-email">
                <Name name={ this.state.name } handlerGenerico={ this.handlerGenerico } />
                <Email name={ this.state.email } handlerGenerico={ this.handlerGenerico } />
                <CPF name={ this.state.cpf } handlerGenerico={ this.handlerGenerico } />
              </div>
          </fieldset>
      </form>
    );
  }
}

export default App;
